  <label for="name">Name: </label>
  <input id="name" type="text" formControlName="name" />
  <!-- <div>
    <span
      style="color: red"
      *ngIf="
        (form.controls.name.invalid && form.controls.name.touched) ||
        (form.controls.name.untouched && isSubmitted)
      "
    >
      Name is required
    </span>
  </div> -->
  <div>
    <span
      style="color: red"
      *ngIf="form.controls.name.errors?.required && isSubmitted"
    >
      Name is required
    </span>
  </div>
  <label for="alias">Alias: </label>
  <input id="alias" type="text" formControlName="alias" />
  <div>
    <span
      style="color: red"
      *ngIf="form.controls.alias.errors?.required && isSubmitted"
    >
      Not Valid
    </span>
  </div>
  <label for="heroes">Heroes</label>

  <select formControlName="brands">
    <option></option>
    <option
      *ngFor="let brand of brands; trackBy: trackByBrandId"
      [ngValue]="brand.id"
    >
      {{ brand.name }}
    </option>
  </select>
  <div>
    <span
      style="color: red"
      *ngIf="form.controls.brands.errors?.required && isSubmitted"
    >
      Not Valid
    </span>
  </div>
  <div>
    <!-- <button [disabled]="!form.valid || !form.dirty">Create</button> -->
    <button>Create</button>
  </div>
</form>
