import { Component, inject } from '@angular/core';
import { FormBuilder, Validators } from '@angular/forms';
import { HeroesService } from '../shared/services/heroes.service';
import { Brands } from '../shared/models/brand.model';

@Component({
  selector: 'app-hero',
  templateUrl: './hero.component.html',
  styleUrls: ['./hero.component.css'],
})
export class HeroComponent {
  fb = inject(FormBuilder);
  heroesService = inject(HeroesService);

  brands: Brands[] = [
    { id: 1, name: 'DC' },
    { id: 2, name: 'Marvel' },
  ];

  heroForm = this.fb.group({
    name: ['', Validators.required],
    alias: ['', Validators.required],
    brands: ['', Validators.required],
  });

  trackByBrandId(index: any, item: Brands) {
    return item.id;
  }

  submit(values: any) {
    console.log('values', values);
    let x = this.heroForm.controls.brands.value?.split('');
    console.log('ccc ', x);

    const hero = {
      name: this.heroForm.controls.name.value,
      alias: this.heroForm.controls.alias.value,
      brandId: this.heroForm.controls.brands.value,
      // brandName: this.heroForm.controls.brands.controls.id.value,
    };
    console.log('herottt', hero);
    this.heroesService.createHero$.next(values);
  }
}
<form [formGroup]="heroForm" (ngSubmit)="submit(heroForm.value)">
  <label for="name">Name: </label>
  <input id="name" type="text" formControlName="name" />
  <label for="alias">Alias: </label>
  <input id="alias" type="text" formControlName="alias" />
  <label for="heroes">Heroes</label>
  <!-- <div formGroupName="brands"> -->
  <select formControlName="brands">
    <option></option>
    <option
      *ngFor="let brand of brands; trackBy: trackByBrandId"
      [ngValue]="brand"
    >
      {{ brand.name }}
    </option>
  </select>
  <!-- </div> -->
  <div>
    <button [disabled]="!heroForm.valid || !heroForm.dirty">Create</button>
    {{ heroForm.valid }}
  </div>
</form>
