import { HttpClient } from '@angular/common/http';
import { Injectable, inject } from '@angular/core';
import { Subject, combineLatest, map, startWith, switchMap, tap } from 'rxjs';
import { AddHero, Hero } from '../models/hero.model';

@Injectable({
  providedIn: 'root',
})
export class HeroesService {
  httpClient = inject(HttpClient);

  updateHeroStatus$ = new Subject<number>();
  createHero$ = new Subject<{
    name: string | null;
    alias: string | null;
    brands: string | null;
  }>();

  private heroCreated$ = this.createHero$.pipe(
    tap((hero) => console.log('hero: ', hero.brands))
    // map((hero) => ({
    //   name: hero.name,
    //   alias: hero.alias,
    //   brandId: hero.brands.

    // }))
    // switchMap((hero) => this.httpClient.post<AddHero>('/heroes', hero))
  );

  private heroStatus$ = this.updateHeroStatus$.pipe(
    tap((hero) => console.log('Let update a hero:', hero)),
    switchMap((hero) => this.httpClient.put<Hero>(`/heroes/${hero}`, hero)),
    tap((res) => console.log('Updated: ', res))
  );

  private listOfheroes$ = this.httpClient.get<Hero[]>('/heroes');

  heroes$ = combineLatest([
    this.listOfheroes$,
    this.heroStatus$.pipe(startWith(1)),
  ]).pipe(switchMap(() => this.httpClient.get<Hero[]>('/heroes')));

  constructor() {
    this.heroes$.pipe(tap((res) => console.log('response:', res))).subscribe();
    this.heroStatus$.subscribe();
    this.heroCreated$
      .pipe(tap((hero) => console.log('Create Hero: ', hero)))
      .subscribe();
  }

  disableHero(hero: Hero) {
    console.log('Event', hero);
  }
}
