import { HttpClient } from '@angular/common/http';
import { Injectable, inject } from '@angular/core';
import { Subject, map, switchMap, tap } from 'rxjs';
import { Hero } from '../models/hero';

@Injectable({
  providedIn: 'root',
})
export class HeroesService {
  httpClient = inject(HttpClient);

  updateHeroStatus$ = new Subject<number>();

  heroStatus$ = this.updateHeroStatus$.pipe(
    tap((hero) => console.log('Let update a hero:', hero)),
    switchMap((hero) => this.httpClient.put<Hero>(`/heroes${hero}`, hero)),
    tap((res) => console.log('Updated: ', res))
  );

  heroes$ = this.httpClient.get<Hero[]>('/heroes');

  constructor() {
    this.heroes$.pipe(tap((res) => console.log('response:', res))).subscribe();
    this.heroStatus$.subscribe();
  }

  disableHero(hero: Hero) {
    console.log('Event', hero);
  }
}
